window.__NUXT__=function(e,t,a,n,s){return{layout:"default",data:[{contact:{title:[{type:"heading1",text:"Information",spans:[]}],image:{dimensions:{width:2048,height:1365},alt:s,copyright:s,url:"https://images.prismic.io/amanda-blog/fbfbd4f5-33b6-458a-b4a2-d4a4b13978d6_IMG_2683.JPG?auto=compress,format"},intro:[{type:e,text:"Here is brief outline of what I offer:",spans:[]},{type:e,text:"Currently Amanda is offering all of her counselling and coaching services virtually.",spans:[]}],membership:[{type:e,text:"Online Members Community 2021  for only £99.   It includes 12 months support via our private members group and twelve 21-day online monthly programmes throughout the year.  The programmes will relate to different aspects of holistic health and well-being and are designed to offer as much support at an affordable price during the coming year of readjustment. ",spans:[{start:0,end:31,type:t}]}],counselling:[{type:e,text:"Face-to-face counselling £120 per session or £690 for 6 sessions.",spans:[{start:0,end:25,type:t}]}],support:[{type:e,text:"Online monthly support. This is usually a programme for clients once they have been in full therapy for 6 weeks or more. It very much depends on the nature of the problem as to whether this is an option initially.",spans:[{start:0,end:22,type:t}]},{type:e,text:"£165 per month (4 x 20 minute sessions). Only available as follow-up.",spans:[]}],adolescents:[{type:e,text:"A unique service to adolescents in terms of a counselling with continuous support. Availability via instant messaging when an adolescent is in counselling. ",spans:[{start:0,end:31,type:t}]},{type:e,text:"Mentoring support service to adoptees and a programme to help them through the minefield of finding birth parents/families and dealing with a challenging set of consequences - whatever the outcome may be.",spans:[{start:0,end:38,type:t}]},{type:e,text:"£160 per month.  Email support only.",spans:[]}],payment:[{type:e,text:"Payment is required in advance of the sessions.  The type of sessions you may require can be established after the first session and Amanda will happily refer you on to another qualified counsellor if she feels she can't help you. Payment can be made through PayPal by selecting an option below. ",spans:[]}],cancellation:[{type:e,text:"Appointments are made to reserve a specific time for you; therefore, a minimum of 24 hours notice is required for canceling or rescheduling an appointment. The full fee will be charged for appointments that are missed, cancelled or rescheduled without 24 hours notice.",spans:[{start:156,end:268,type:t}]}],offer:[{type:e,text:"Residential programmes written for individuals, couples or families and delivered in a non-clinical setting at Praana Wellness Centre. Particularly mental health issues such as chronic anxiety or depression or lifestyle challenges such as addiction. or couples we have delivered intensive weekend programmes for couples in crisis, 2 to 3+ week programmes for young people managing eating disorders and addiction, 2 week+ programmes healthy eating for controlling chronic disease symptoms and family therapy to help improve relationships between parents and older children.",spans:[{start:0,end:23,type:t},{start:87,end:108,type:t},{start:111,end:133,type:a,data:{link_type:n,url:"https://praanawellness.com/."}},{start:177,end:192,type:t},{start:196,end:206,type:t},{start:238,end:248,type:t},{start:312,end:329,type:t},{start:381,end:411,type:t},{start:432,end:478,type:t},{start:492,end:506,type:t}]},{type:e,text:"Please email me for more details.",spans:[{start:7,end:14,type:a,data:{link_type:n,url:"mailto:amanda@praanawellness.com"}}]}],"offer-details":[{type:"heading6",text:"",spans:[]}],email:[{type:e,text:"amanda@praanawellness.com",spans:[{start:0,end:25,type:a,data:{link_type:n,url:"mailto: praanawellness@gmail.com"}}]}],telephone:[{type:e,text:"+33 (6) 40 33 23 82",spans:[{start:0,end:19,type:a,data:{link_type:n,url:"tel: +33 (6) 40 33 23 82"}}]}],location:[{type:e,text:"Chez Vallée",spans:[]},{type:e,text:"Saint Germain de Vibrac",spans:[]},{type:e,text:"17500 Charente Maritime",spans:[]},{type:e,text:"France",spans:[]}]}}],fetch:[],error:s,serverRendered:!0,routePath:"/contact",config:{}}}("paragraph","strong","hyperlink","Web",null)